import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import MaterialTable from "material-table";
import TableContainer from "@material-ui/core/TableContainer";

// import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Container from "@material-ui/core/Container";
import CircularProgress from "@material-ui/core/CircularProgress";
import Grid from "@material-ui/core/Grid";
import DeleteIcon from "@material-ui/icons/Delete";
import IconButton from "@material-ui/core/IconButton";
import { Alert, AlertTitle } from "@material-ui/lab";
// import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import ExpansionPanel from "@material-ui/core/ExpansionPanel";
import ExpansionPanelSummary from "@material-ui/core/ExpansionPanelSummary";
import ExpansionPanelDetails from "@material-ui/core/ExpansionPanelDetails";
import Typography from "@material-ui/core/Typography";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";

import { makeStyles, useTheme, withStyles } from "@material-ui/core/styles";
import { authAxios } from "../../utils";
import { orderListURL, orderDeleteURL, orderAddressURL } from "../../constants";

const styles = theme => ({
  table: {
    minWidth: 500
  },
  orderDetails: {
    background: "#3f51b5"
  },
  orderDetailCell: {
    background: "#00bcd4"
  },
  orderDetailTotal: {
    background: "#304ffe"
  }
});

class Customer extends React.Component {
  state = {
    orders: [],
    loading: false,
    error: null,
    message: null,
    orderAddress: null
  };

  componentDidMount() {
    this.FetchOrders();
    // console.log(orders);
  }

  FetchOrders = () => {
    this.setState({ loading: true });
    authAxios
      .get(orderListURL)
      .then(res => {
        this.setState({ orders: res.data, loading: false });
        // console.log(res.data);
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };

  deleteOrder = id => {
    authAxios
      .delete(orderDeleteURL(id))
      .then(res => {
        this.setState({ message: "Order Deleted" });
        this.handleCallback();
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };

  handleCallback = () => {
    this.FetchOrders();
  };

  fetchOrderAddress = address_id => {
    authAxios
      .get(orderAddressURL(address_id))
      .then(res => {
        this.setState({ orderAddress: res.data, loading: false });
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };

  render() {
    const { classes } = this.props;
    const { orders, loading, message, orderAddress } = this.state;
    // console.log(orderAddress);

    const columns = [
      { title: "Order No", field: "id" },
      { title: "Shop Name", field: "shop_name" },
      { title: "Data", field: "start_date" },
      { title: "Status", field: "order_status" }
    ];

    return (
      <Container>
        {message && (
          <Alert severity="error">
            <AlertTitle>{message}</AlertTitle>
            {/* This is an error alert — <strong>check it out!</strong> */}
          </Alert>
        )}
        {loading && (
          <div
            align="middle"
            // className={classes.loading}
            style={{ display: "flex", justifyContent: "center" }}
          >
            <CircularProgress />
          </div>
        )}
        <Grid item xs={12}>
          <MaterialTable
            options={
              ({
                headerStyle: {
                  // backgroundColor: "#01579b",#bbdefb
                  backgroundColor: "#9fa8da",
                  color: "#212121"
                },
                rowStyle: {
                  backgroundColor: "#e8eaf6"
                }
              },
              {
                pageSize: 20,
                pageSizeOptions: [20, 50, 100]
              })
            }
            title="Orders"
            columns={columns}
            data={orders}
            pageSize={10}
            detailPanel={rowData => {
              const addressID = rowData.shipping_address;
              const orderItem = rowData.order_items;
              const { orderAddress } = this.state;
              // console.log(orderAddress);
              // console.log(orderAddress);
              return (
                <div>
                  <Grid item xs={12}>
                    <ExpansionPanel>
                      <ExpansionPanelSummary
                        // expandIcon={<ExpandMoreIcon />}
                        aria-controls="panel1a-content"
                        id="panel1a-header"
                      >
                        <IconButton>
                          <ExpandMoreIcon
                            onClick={() => this.fetchOrderAddress(addressID)}
                          />
                        </IconButton>
                        <Typography>Customer Address</Typography>
                      </ExpansionPanelSummary>
                      {orderAddress && (
                        <ExpansionPanelDetails>
                          {/* <Typography>
                            {orderAddress.place}
                            {orderAddress.area}
                            {orderAddress.road_name}
                            {orderAddress.house_name}
                            {orderAddress.village}
                          </Typography> */}
                          <Card className={classes.root}>
                            <CardContent>
                              <Typography
                                className={classes.title}
                                color="textSecondary"
                                gutterBottom
                              >
                                Delivery Address
                              </Typography>
                              <Typography variant="h5" component="h2">
                                {orderAddress.place}
                              </Typography>
                              <Typography
                                className={classes.pos}
                                color="textSecondary"
                              >
                                {orderAddress.area}
                              </Typography>
                              <Typography variant="body2" component="p">
                                {orderAddress.road_name}
                                <br />
                                {orderAddress.house_name}
                                <br />
                                {orderAddress.village}
                                <br />
                                {'"a benevolent smile"'}
                              </Typography>
                            </CardContent>
                            <CardActions>
                              <Button size="small">
                                {orderAddress.phone_number}
                              </Button>
                            </CardActions>
                          </Card>
                        </ExpansionPanelDetails>
                      )}
                    </ExpansionPanel>
                    <TableContainer component={Paper}>
                      <Table
                        className={classes.table}
                        aria-label="spanning table"
                      >
                        <TableHead>
                          <TableRow className={classes.orderDetails}>
                            <TableCell align="center" colSpan={4}>
                              <Typography
                                variant="h5"
                                component="h5"
                                gutterBottom
                              >
                                Order Details
                              </Typography>
                              <Button
                                variant="contained"
                                color="secondary"
                                onClick={() => this.deleteOrder(rowData.id)}
                              >
                                Delete Order
                              </Button>
                              <Button
                                variant="contained"
                                color="primary"
                                onClick={() =>
                                  this.fetchOrderAddress(addressID)
                                }
                              >
                                User Address
                              </Button>
                            </TableCell>
                          </TableRow>
                          <TableRow className={classes.orderDetailCell}>
                            <TableCell>Item Name</TableCell>
                            <TableCell align="right">Item Price</TableCell>
                            <TableCell align="right">Quantity</TableCell>
                            <TableCell align="right">Total Price</TableCell>
                          </TableRow>
                        </TableHead>
                        <TableBody>
                          {orderItem.map(row => (
                            <TableRow
                              key={row.desc}
                              className={classes.orderDetailCell}
                            >
                              <TableCell>{row.item.title}</TableCell>
                              <TableCell align="right">
                                {row.quantity}
                              </TableCell>
                              <TableCell align="right">
                                {row.quantity}
                              </TableCell>
                              <TableCell align="right">
                                {row.final_price}
                              </TableCell>
                            </TableRow>
                          ))}

                          <TableRow className={classes.orderDetailTotal}>
                            <TableCell rowSpan={3} />
                            <TableCell align="right" colSpan={2}>
                              Total
                            </TableCell>
                            <TableCell align="right">{rowData.total}</TableCell>
                          </TableRow>
                        </TableBody>
                      </Table>
                    </TableContainer>
                  </Grid>
                </div>
              );
            }}
          />
        </Grid>
      </Container>
    );
  }
}

const mapStateToProps = state => {
  return {
    isAuthenticated: state.auth.token !== null
  };
};

// export default connect(mapStateToProps)(Profile);

export default connect(mapStateToProps)(withStyles(styles)(Customer));
