import React, { Component } from "react";
import { Link } from "react-router-dom";
import { Button, Container, Header, Select } from "semantic-ui-react";

import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import { makeStyles } from "@material-ui/core/styles";
import CircularProgress from "@material-ui/core/CircularProgress";
import { Alert, AlertTitle } from "@material-ui/lab";
import AddIcon from "@material-ui/icons/Add";

import { authAxios } from "../../utils";
import { checkoutURL, orderSummaryURL, addressListURL } from "../../constants";

const useStyles = makeStyles(theme => ({
  root: {
    width: "100%",
    marginTop: theme.spacing.unit * 3,
    overflowX: "auto"
  },
  table: {
    minWidth: 200
  },
  tableHead: {
    background: "#3f51b5",
    color: "white",
    fontWeigth: "bold",
    fontSize: "20pt"
  },
  tablecell: {
    fontSize: "12pt"
  }
}));

const OrderPreview = props => {
  const { data } = props;
  const classes = useStyles();

  return (
    <React.Fragment>
      <div className={classes.TopPadding} style={{ width: 400 }}>
        <TableContainer component={Paper}>
          <Table
            className={classes.table}
            aria-label="customized table"
            style={{ width: 400 }}
          >
            <TableHead>
              <TableRow
                // style={{
                //   backgroundColor: "#3f51b5",
                //   color: "white",
                //   fontSize: 20
                // }}
                className={classes.tableHead}
              >
                <TableCell align="left">Item</TableCell>
                <TableCell align="left">Quantity</TableCell>
                <TableCell align="left">Price</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {data && (
                <React.Fragment>
                  {data.order_items.map(order_item => {
                    return (
                      <TableRow key={order_item.id}>
                        <TableCell component="th" scope="row">
                          {order_item.item.title}
                        </TableCell>
                        <TableCell component="th" scope="row">
                          {order_item.quantity}
                        </TableCell>
                        <TableCell align="left">
                          {order_item.final_price}
                        </TableCell>
                      </TableRow>
                    );
                  })}
                  <TableRow
                    style={{
                      backgroundColor: "#9fa8da",
                      color: "white",
                      fontSize: 20
                    }}
                  >
                    {/* <TableCell rowSpan={2} /> */}
                    <TableCell colSpan={2}>Total</TableCell>
                    <TableCell align="left">{data.total}</TableCell>
                  </TableRow>
                </React.Fragment>
              )}
            </TableBody>
          </Table>
        </TableContainer>
      </div>
    </React.Fragment>
  );
};

class CheckoutForm extends Component {
  state = {
    data: null,
    loading: false,
    error: null,
    success: false,
    shippingAddresses: [],
    billingAddresses: [],
    selectedShippingAddress: null,
    shop: "",
    place: "",
    mode_of_payment: ""
  };

  componentDidMount() {
    this.handleFetchOrder();
    this.handleFetchShippingAddresses();
  }

  handleFetchShippingAddresses = () => {
    this.setState({ loading: true });
    authAxios
      .get(addressListURL("S"))
      .then(res => {
        this.setState({
          shippingAddresses: res.data.map(a => {
            return {
              key: a.id,
              text: `${a.place}, ${a.area}, ${a.road_name}`,
              value: a.id
            };
          }),
          selectedShippingAddress: this.handleGetDefaultAddress(res.data),

          loading: false
        });
      })
      .catch(err => {
        this.setState({ error: err, loading: false });
      });
  };

  handleGetDefaultAddress = addresses => {
    const filteredAddresses = addresses.filter(el => el.default === true);
    if (filteredAddresses.length > 0) {
      return filteredAddresses[0].id;
    }
    return "";
  };

  handleFetchOrder = () => {
    this.setState({ loading: true });
    authAxios
      .get(orderSummaryURL)
      .then(res => {
        this.setState({ data: res.data, loading: false });
      })
      .catch(err => {
        if (err.response.status === 404) {
          this.props.history.push("/orders");
        } else {
          this.setState({ error: err, loading: false });
        }
      });
  };

  submit = ev => {
    ev.preventDefault();
    this.setState({ loading: true });
    const { selectedShippingAddress } = this.state;
    authAxios
      .post(checkoutURL, {
        selectedShippingAddress
      })
      .then(res => {
        this.setState({ loading: false, success: true });
        this.redirectToOrders();
      })
      .catch(err => {
        this.setState({ loading: false, error: err });
      });
  };

  redirectToOrders() {
    setTimeout(this.props.history.push("/orders"), 10000);
  }

  render() {
    const { classes } = this.props;
    const {
      data,
      error,
      loading,
      success,
      shippingAddresses,
      selectedShippingAddress
    } = this.state;

    return (
      <div>
        <Container>
          {loading && (
            <div>
              <CircularProgress />
              <CircularProgress color="secondary" />
            </div>
          )}
          {error && (
            <Alert severity="error">
              <AlertTitle>{JSON.stringify(error)}</AlertTitle>
            </Alert>
          )}
          <OrderPreview data={data} />

          <Header>Select Address</Header>
          {shippingAddresses.length > 0 ? (
            <Select
              name="selectedShippingAddress"
              value={selectedShippingAddress}
              clearable
              options={shippingAddresses}
              selection
              onChange={event =>
                this.handleSelectChangeAddress(event.target.value)
              }
            />
          ) : (
            <p>
              You need to <Link to="/profile">add an address</Link>
            </p>
          )}

          {shippingAddresses.length < 1 ? (
            <p>You need to add an addresse to complete your purchase</p>
          ) : (
            <React.Fragment>
              <Header>Confirm Order?</Header>

              {success && (
                <Alert severity="success">
                  <AlertTitle>{"Your Order is successful"}</AlertTitle>
                  {/* This is an error alert — <strong>check it out!</strong> */}
                </Alert>
              )}
              <Button
                loading={loading}
                disabled={loading}
                primary
                onClick={this.submit}
                style={{ marginTop: "10px" }}
              >
                Submit
              </Button>
            </React.Fragment>
          )}
        </Container>
      </div>
    );
  }
}

const mapDispatchToProps = dispatch => {
  return {
    refreshCart: () => dispatch(fetchCart())
  };
};

// export default connect(
//   null,
//   mapDispatchToProps
// )(withStyles(theme)(CheckoutForm));

export default connect(
  null,
  mapDispatchToProps
)(CheckoutForm);
