import React, { useState, useEffect } from "react";
import MaterialTable from "material-table";
import TableContainer from "@material-ui/core/TableContainer";

import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Container from "@material-ui/core/Container";
import CircularProgress from "@material-ui/core/CircularProgress";

import { authAxios } from "../../utils";
import { orderListURL } from "../../constants";

const useStyles = makeStyles({
  table: {
    minWidth: 700
  }
});

function Customer() {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(false);

  const classes = useStyles();

  const columns = [
    { title: "Shop Name", field: "shop_name" },
    { title: "Total Amount", field: "total" },
    { title: "Data", field: "start_date" },
    { title: "Status", field: "order_status" }
  ];

  useEffect(() => {
    const fetchOrders = async () => {
      setLoading(true);
      const res = await authAxios.get(orderListURL);
      setOrders(res.data);
      setLoading(false);
    };

    fetchOrders();
  }, []);

  return (
    <Container>
      {loading && (
        <div
          align="middle"
          className={classes.loading}
          style={{ display: "flex", justifyContent: "center" }}
        >
          <CircularProgress />
        </div>
      )}
      <MaterialTable
        options={
          ({
            headerStyle: {
              // backgroundColor: "#01579b",#bbdefb
              backgroundColor: "#9fa8da",
              color: "#212121"
            },
            rowStyle: {
              backgroundColor: "#e8eaf6"
            }
          },
          {
            pageSize: 20,
            pageSizeOptions: [20, 50, 100]
          })
        }
        title="Orders"
        columns={columns}
        data={orders}
        pageSize={10}
        detailPanel={rowData => {
          const orderItem = rowData.order_items;

          return (
            <div>
              <TableContainer component={Paper}>
                <Table className={classes.table} aria-label="spanning table">
                  <TableHead>
                    <TableRow>
                      <TableCell align="center" colSpan={4}>
                        Order Details
                      </TableCell>
                      {/* <TableCell align="right">Price</TableCell> */}
                    </TableRow>
                    <TableRow>
                      <TableCell>Item Name</TableCell>
                      <TableCell align="right">Item Price</TableCell>
                      <TableCell align="right">Quantity</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {orderItem.map(row => (
                      <TableRow key={row.desc}>
                        <TableCell>{row.item.title}</TableCell>
                        <TableCell align="right">{row.quantity}</TableCell>
                        <TableCell align="right">{row.final_price}</TableCell>
                      </TableRow>
                    ))}

                    <TableRow>
                      <TableCell rowSpan={2} />
                      <TableCell colSpan={2}>Total</TableCell>
                      <TableCell align="right">{rowData.total}</TableCell>
                    </TableRow>
                  </TableBody>
                </Table>
              </TableContainer>
            </div>
          );
        }}
      />
    </Container>
  );
}
export default Customer;
