import React, { useState, useEffect, useCallback } from "react";
import MaterialTable from "material-table";
import TableContainer from "@material-ui/core/TableContainer";

import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Container from "@material-ui/core/Container";
import CircularProgress from "@material-ui/core/CircularProgress";
import Grid from "@material-ui/core/Grid";
import DeleteIcon from "@material-ui/icons/Delete";
import IconButton from "@material-ui/core/IconButton";

import { authAxios } from "../../utils";
import { orderListURL, orderDeleteURL } from "../../constants";

const useStyles = makeStyles({
  table: {
    minWidth: 500
  },
  orderDetails: {
    background: "#3f51b5"
  },
  orderDetailCell: {
    background: "#c5cae9"
  },
  orderDetailTotal: {
    background: "#304ffe"
  }
});

function Customer() {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const classes = useStyles();

  const columns = [
    { title: "Shop Name", field: "shop_name" },
    { title: "Total Amount", field: "total" },
    { title: "Data", field: "start_date" },
    { title: "Status", field: "order_status" }
  ];

  const fetchOrders = useCallback(() => {
    const fetchOrders1 = async () => {
      setLoading(true);
      const res = await authAxios.get(orderListURL);
      setOrders(res.data);
      setLoading(false);
    };
  });

  useEffect(() => {
    fetchOrders();
    // The effect calls innerFunction, hence it should declare it as a dependency
    // Otherwise, if something about innerFunction changes (e.g. the data it uses), the effect would run the outdated version of innerFunction
  }, [fetchOrders]);

  // const deleteOrder = id => {
  //   authAxios
  //     .delete(orderDeleteURL(id))
  //     .then(res => {
  //       handleCallback();
  //     })
  //     .catch(err => {
  //       setError(err);
  //     });
  // };

  // useEffect(() => {
  //   const fetchOrders = async () => {
  //     setLoading(true);
  //     const res = await authAxios.get(orderListURL);
  //     setOrders(res.data);
  //     setLoading(false);
  //   };
  //   fetchOrders();
  // }, []);

  // const deleteOrder = orderID => {
  //   authAxios
  //     .delete(orderDeleteURL(orderID))
  //     .then(res => {
  //       setOrders(res.data);
  //     })
  //     .catch(err => {
  //       setError(err);
  //     });
  // };

  return (
    <Container>
      {loading && (
        <div
          align="middle"
          className={classes.loading}
          style={{ display: "flex", justifyContent: "center" }}
        >
          <CircularProgress />
        </div>
      )}
      <Grid item xs={12}>
        <MaterialTable
          options={
            ({
              headerStyle: {
                // backgroundColor: "#01579b",#bbdefb
                backgroundColor: "#9fa8da",
                color: "#212121"
              },
              rowStyle: {
                backgroundColor: "#e8eaf6"
              }
            },
            {
              pageSize: 20,
              pageSizeOptions: [20, 50, 100]
            })
          }
          title="Orders"
          columns={columns}
          data={orders}
          pageSize={10}
          detailPanel={rowData => {
            const orderItem = rowData.order_items;

            return (
              <div>
                <Grid item xs={12}>
                  <TableContainer component={Paper}>
                    <Table
                      className={classes.table}
                      aria-label="spanning table"
                    >
                      <TableHead>
                        <TableRow className={classes.orderDetails}>
                          <TableCell align="center" colSpan={4}>
                            Order Details
                            <IconButton onClick={deleteOrder(rowData.id)}>
                              {/* onClick={deleteOrder(rowData.id)} */}
                              <DeleteIcon />
                            </IconButton>
                          </TableCell>
                          {/* <TableCell align="right">Price</TableCell> */}
                        </TableRow>
                        <TableRow className={classes.orderDetailCell}>
                          <TableCell>Item Name</TableCell>
                          <TableCell align="right">Item Price</TableCell>
                          <TableCell align="right">Quantity</TableCell>
                          <TableCell align="right">Total Price</TableCell>
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        {orderItem.map(row => (
                          <TableRow
                            key={row.desc}
                            className={classes.orderDetailCell}
                          >
                            <TableCell>{row.item.title}</TableCell>
                            <TableCell align="right">{row.quantity}</TableCell>
                            <TableCell align="right">{row.quantity}</TableCell>
                            <TableCell align="right">
                              {row.final_price}
                            </TableCell>
                          </TableRow>
                        ))}

                        <TableRow className={classes.orderDetailTotal}>
                          <TableCell rowSpan={3} />
                          <TableCell align="right" colSpan={2}>
                            Total
                          </TableCell>
                          <TableCell align="right">{rowData.total}</TableCell>
                        </TableRow>
                      </TableBody>
                    </Table>
                  </TableContainer>
                </Grid>
              </div>
            );
          }}
        />
      </Grid>
    </Container>
  );
}
export default Customer;
