import React, { Component } from "react";
import {
  Button,
  Container,
  Dimmer,
  Divider,
  Form,
  Header,
  Image,
  Item,
  Label,
  Loader,
  Message,
  Segment,
  Select,
  Radio
} from "semantic-ui-react";

import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import { makeStyles } from "@material-ui/core/styles";

import { Link, withRouter } from "react-router-dom";
import { authAxios } from "../utils";
import {
  checkoutURL,
  orderSummaryURL,
  addCouponURL,
  addressListURL
} from "../../constants";

const useStyles = makeStyles({
  table: {
    minWidth: 700
  }
});

const OrderPreview = props => {
  const { data } = props;
  const classes = useStyles();

  return (
    <React.Fragment>
      {data && (
        <React.Fragment>
          {data.order_items.map(order_item => {
            return <React.Fragment></React.Fragment>;
          })}
        </React.Fragment>
      )}
    </React.Fragment>
  );
};

class CouponForm extends Component {
  state = {
    code: ""
  };

  handleChange = e => {
    this.setState({
      code: e.target.value
    });
  };

  handleSubmit = e => {
    const { code } = this.state;
    this.props.handleAddCoupon(e, code);
    this.setState({ code: "" });
  };

  render() {
    const { code } = this.state;
    return (
      <React.Fragment>
        <Container>
          <Form onSubmit={this.handleSubmit}>
            <Form.Field>
              <label>Coupon code</label>
              <input
                placeholder="Enter a coupon.."
                value={code}
                onChange={this.handleChange}
              />
            </Form.Field>
            <Button type="submit">Submit</Button>
          </Form>
        </Container>
      </React.Fragment>
    );
  }
}

class CheckoutForm extends Component {
  state = {
    data: null,
    loading: false,
    error: null,
    success: false,
    shippingAddresses: [],
    billingAddresses: [],
    selectedBillingAddress: "",
    selectedShippingAddress: "",
    shop: "",
    place: "",
    mode_of_payment: ""
  };

  componentDidMount() {
    this.handleFetchOrder();
    this.handleFetchBillingAddresses();
    this.handleFetchShippingAddresses();
  }

  handleGetDefaultAddress = addresses => {
    const filteredAddresses = addresses.filter(el => el.default === true);
    if (filteredAddresses.length > 0) {
      return filteredAddresses[0].id;
    }
    return "";
  };

  handleFetchBillingAddresses = () => {
    this.setState({ loading: true });
    authAxios
      .get(addressListURL("B"))
      .then(res => {
        this.setState({
          billingAddresses: res.data.map(a => {
            return {
              key: a.id,
              text: `${a.place}, ${a.area}, ${a.road_name}`,
              value: a.id
            };
          }),
          selectedBillingAddress: this.handleGetDefaultAddress(res.data),
          loading: false
        });
      })
      .catch(err => {
        this.setState({ error: err, loading: false });
      });
  };

  handleFetchShippingAddresses = () => {
    this.setState({ loading: true });
    authAxios
      .get(addressListURL("S"))
      .then(res => {
        this.setState({
          shippingAddresses: res.data.map(a => {
            return {
              key: a.id,
              text: `${a.place}, ${a.area}, ${a.road_name}`,
              value: a.id
            };
          }),
          selectedShippingAddress: this.handleGetDefaultAddress(res.data),

          loading: false
        });
      })
      .catch(err => {
        this.setState({ error: err, loading: false });
      });
  };

  handleFetchOrder = () => {
    this.setState({ loading: true });
    authAxios
      .get(orderSummaryURL)
      .then(res => {
        this.setState({ data: res.data, loading: false });
      })
      .catch(err => {
        if (err.response.status === 404) {
          this.props.history.push("/dashboard");
        } else {
          this.setState({ error: err, loading: false });
        }
      });
  };

  handleAddCoupon = (e, code) => {
    e.preventDefault();
    this.setState({ loading: true });
    authAxios
      .post(addCouponURL, { code })
      .then(res => {
        this.setState({ loading: false });
        this.handleFetchOrder();
      })
      .catch(err => {
        this.setState({ error: err, loading: false });
      });
  };

  handleSelectChange = (e, { name, value }) => {
    this.setState({ [name]: value });
  };

  submit = ev => {
    ev.preventDefault();
    this.setState({ loading: true });
    const {
      selectedBillingAddress,
      selectedShippingAddress,
      mode_of_payment
    } = this.state;
    authAxios
      .post(checkoutURL, {
        selectedBillingAddress,
        selectedShippingAddress,
        mode_of_payment
      })
      .then(res => {
        this.setState({ loading: false, success: true });
      })
      .catch(err => {
        this.setState({ loading: false, error: err });
      });
  };

  render() {
    const {
      data,
      error,
      loading,
      success,
      billingAddresses,
      shippingAddresses,
      selectedBillingAddress,
      selectedShippingAddress,
      mode_of_payment
    } = this.state;
    return (
      <div>
        <Container>
          {error && (
            <Message
              error
              header="There was some errors with your submission"
              content={JSON.stringify(error)}
            />
          )}
          {loading && (
            <Segment>
              <Dimmer active inverted>
                <Loader inverted>Loading</Loader>
              </Dimmer>
              <Image src="/images/wireframe/short-paragraph.png" />
            </Segment>
          )}
          <OrderPreview data={data} />

          <Header>Select a shipping address</Header>
          {shippingAddresses.length > 0 ? (
            <Select
              name="selectedShippingAddress"
              value={selectedShippingAddress}
              clearable
              options={shippingAddresses}
              selection
              onChange={this.handleSelectChange}
            />
          ) : (
            <p>
              You need to <Link to="/profile">add a shipping address</Link>
            </p>
          )}
          <Divider />
          <Header>Select a payment method</Header>
          <Form.Field
            control={Radio}
            label="Cash on Delivery"
            name="mode_of_payment"
            value="Cash on Delivery"
            // checked={value === "Cash on Delivery"}
            onChange={this.handleSelectChange}
          />
          <Form.Field
            control={Radio}
            label="Kadam"
            name="mode_of_payment"
            value="Kadam"
            // checked={value === "Kadam"}
            onChange={this.handleSelectChange}
          />
          <Divider />
          {/* {billingAddresses.length < 1 || shippingAddresses.length < 1 ? ( */}
          {shippingAddresses.length < 1 ? (
            <p>You need to add an addresse to complete your purchase</p>
          ) : (
            <React.Fragment>
              <Header>confirm the purchase?</Header>
              {/* <CardElement /> */}
              {success && (
                <Message positive>
                  <Message.Header>Your Order is successful</Message.Header>
                  <p>
                    Go to your <b>profile</b> to see the order delivery status.
                  </p>
                </Message>
              )}
              <Button
                loading={loading}
                disabled={loading}
                primary
                onClick={this.submit}
                style={{ marginTop: "10px" }}
              >
                Submit
              </Button>
            </React.Fragment>
          )}
        </Container>
      </div>
    );
  }
}

export default CheckoutForm;
