import React from "react";
import { connect } from "react-redux";
import axios from "axios";
// import Button from "react-bootstrap/Button";
import Layout from "react-bootstrap/Button";
import {
  Container,
  Dimmer,
  Image,
  Item,
  Card,
  Icon,
  Loader,
  Message,
  Segment,
  Label,
  Button,
  Select,
  Divider,
  Form
} from "semantic-ui-react";
import { addToCartURL, ShopProductListURL } from "../constants";
import { fetchCart } from "../store/actions/cart";
import { authAxios } from "../utils";

class ShopProductList extends React.Component {
  state = {
    loading: false,
    error: null,
    data: [],
    formData: {}
  };

  componentDidMount() {
    this.handleFetchItem();
  }

  handleToggleForm = () => {
    const { formVisible } = this.state;
    this.setState({
      formVisible: !formVisible
    });
  };

  handleFetchItem = () => {
    const {
      match: { params }
    } = this.props;
    this.setState({ loading: true });
    axios
      .get(productDetailURL(params.productID))
      .then(res => {
        this.setState({ data: res.data, loading: false });
      })
      .catch(err => {
        this.setState({ error: err, loading: false });
      });
  };

  handleFormatData = formData => {
    // convert {colour: 1, size: 2} to [1,2] - they're all variations
    return Object.keys(formData).map(key => {
      return formData[key];
    });
  };

  handleAddToCart = slug => {
    this.setState({ loading: true });
    const { formData } = this.state;
    const variations = this.handleFormatData(formData);
    authAxios
      .post(addToCartURL, { slug, variations })
      .then(res => {
        this.props.refreshCart();
        this.setState({ loading: false });
      })
      .catch(err => {
        this.setState({ error: err, loading: false });
      });
  };

  handleChange = (e, { name, value }) => {
    const { formData } = this.state;
    const updatedFormData = {
      ...formData,
      [name]: value
    };
    this.setState({ formData: updatedFormData });
  };
  render() {
    const { data, error, formData, formVisible, loading } = this.state;
    const item = data;
    return (
      <Container>
        {error && (
          <Message
            error
            header="There was some errors with your submission"
            content={JSON.stringify(error)}
          />
        )}
        {loading && (
          <Segment>
            <Dimmer active inverted>
              <Loader inverted>Loading</Loader>
            </Dimmer>

            <Image src="/images/wireframe/short-paragraph.png" />
          </Segment>
        )}
        <Item.Group divided>
          {data.map(item => {
            return (
              <React.Fragment>
                <Divider />
                <Form onSubmit={() => this.handleAddToCart(item.slug)}>
                  {data.variations.map(v => {
                    const name = v.name.toLowerCase();
                    return (
                      <Form.Field key={v.id}>
                        <Select
                          name={name}
                          onChange={this.handleChange}
                          placeholder={`Select a ${name}`}
                          fluid
                          selection
                          options={v.item_variations.map(item => {
                            return {
                              key: item.id,
                              text: item.value,
                              value: item.id
                            };
                          })}
                          value={formData[name]}
                        />
                      </Form.Field>
                    );
                  })}
                  <Form.Button primary>Add</Form.Button>
                </Form>
              </React.Fragment>
            );
          })}
        </Item.Group>
      </Container>
    );
  }
}
const mapDispatchToProps = dispatch => {
  return {
    refreshCart: () => dispatch(fetchCart())
  };
};

export default connect(
  null,
  mapDispatchToProps
)(ShopProductList);
