import React from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { makeStyles } from "@material-ui/core/styles";
import Box from "@material-ui/core/Box";
import Collapse from "@material-ui/core/Collapse";
import IconButton from "@material-ui/core/IconButton";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Typography from "@material-ui/core/Typography";
import Paper from "@material-ui/core/Paper";
import KeyboardArrowDownIcon from "@material-ui/icons/KeyboardArrowDown";
import KeyboardArrowUpIcon from "@material-ui/icons/KeyboardArrowUp";

import { userIDURL, userTypeURL, orderListURL } from "../../constants";

import { authAxios } from "../../utils";
const useRowStyles = makeStyles({
  root: {
    "& > *": {
      borderBottom: "unset"
    }
  }
});

function Row(props) {
  const { order } = props;
  //   const { orders } = props;
  const [open, setOpen] = React.useState(false);
  const classes = useRowStyles();

  return (
    <React.Fragment>
      <TableRow className={classes.root}>
        <TableCell>
          <IconButton
            aria-label="expand row"
            size="small"
            onClick={() => setOpen(!open)}
          >
            {open ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
          </IconButton>
        </TableCell>
        <TableCell component="th" scope="row">
          {order.shop_name}
        </TableCell>
        {/* <TableCell align="right">{order.shop_name}</TableCell> */}
        <TableCell align="right">{order.total}</TableCell>
        <TableCell align="right">{order.start_date}</TableCell>
        <TableCell align="right">{order.order_status}</TableCell>
      </TableRow>
      <TableRow>
        <TableCell style={{ paddingBottom: 0, paddingTop: 0 }} colSpan={6}>
          <Collapse in={open} timeout="auto" unmountOnExit>
            <Box margin={1}>
              <Typography variant="h6" gutterBottom component="div">
                History
              </Typography>
              <Table size="small" aria-label="purchases">
                <TableHead>
                  <TableRow>
                    <TableCell>Item</TableCell>

                    <TableCell align="right">Quantity</TableCell>
                    <TableCell align="right">Total price</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {order.order_items.map(order_item => (
                    <TableRow key={order_item.id}>
                      <TableCell component="th" scope="row">
                        {order_item.item.title}
                      </TableCell>

                      <TableCell align="right">{order_item.quantity}</TableCell>
                      <TableCell align="right">
                        {order_item.final_price}
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </Box>
          </Collapse>
        </TableCell>
      </TableRow>
    </React.Fragment>
  );
}

function CollapsibleTable(props) {
  const { orders } = props;
  const { UserType } = props;

  return (
    <TableContainer component={Paper}>
      <Table aria-label="collapsible table">
        <TableHead>
          <TableRow>
            <TableCell />
            {/* <TableCell>{UserType}</TableCell> */}
            <TableCell align="right">Shop Name</TableCell>
            <TableCell align="right">Total Amount</TableCell>
            <TableCell align="right">Date</TableCell>
            <TableCell align="right">Order Status</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {orders.map(order => (
            <Row key={order.id} order={order} />
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
}

class Orders extends React.Component {
  state = {
    orders: [],
    userID: null,
    UserType: null
  };

  componentDidMount() {
    this.handleFetchOrders();
    this.handleFetchUserID();
    this.handleFetchUserType();
  }

  handleFetchUserID = () => {
    authAxios
      .get(userIDURL)
      .then(res => {
        this.setState({ userID: res.data.userID });
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };

  handleFetchUserType = () => {
    authAxios
      .get(userTypeURL)
      .then(res => {
        this.setState({ UserType: res.data.UserType });
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };

  handleFetchOrders = () => {
    this.setState({ loading: true });
    authAxios
      .get(orderListURL)
      .then(res => {
        this.setState({
          loading: false,
          orders: res.data
        });
      })
      .catch(err => {
        this.setState({ error: err, loading: false });
      });
  };

  render() {
    const { orders } = this.state;
    const { userID } = this.state;
    const { UserType } = this.state;

    return (
      <div>
        <CollapsibleTable orders={orders} UserType={UserType} />
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    isAuthenticated: state.auth.token !== null
  };
};

export default connect(mapStateToProps)(Orders);
