class AddressForm extends React.Component {
  // constructor() {
  //   super();
  // }
  state = {
    error: null,
    loading: false,
    formData: {
      house_name: "",
      place: "",
      area: "",
      road_name: "",
      village: "",
      district: "",
      state: "",
      pin_code: "",
      phone_number: "",
      default: false,
      id: "",
      user: 1
    },
    saving: false,
    success: false,
    selectedPlace: "Kolapparamb",
    areas: [],
    places: []
  };

  componentDidMount() {
    const { address, formType } = this.props;
    if (formType === UPDATE_FORM) {
      this.setState({ formData: address });
    }
    this.fetchPlaces();
    // this.fetchAreas();
  }

  handleToggleDefault = () => {
    const { formData } = this.state;
    const updatedFormdata = {
      ...formData,
      default: !formData.default
    };
    this.setState({
      formData: updatedFormdata
    });
  };

  handleChange = e => {
    const { formData } = this.state;
    const updatedFormdata = {
      ...formData,
      [e.target.name]: e.target.value
    };
    this.setState({
      formData: updatedFormdata
    });
  };

  handleChangePlace = e => {
    const { formData } = this.state;
    const updatedFormdata = {
      ...formData,
      [e.target.name]: e.target.value
    };
    this.setState({
      formData: updatedFormdata
    });
    // this.fetchAreas();
    // console.log("fetching area");
  };

  fetchPlaces = () => {
    this.setState({ loading: true });
    console.log("mounted");
    authAxios
      .get(placeListURL)
      .then(res => {
        this.setState({ places: res.data, loading: false });
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };

  // fetchAreas = () => {
  //   const { selectedPlace } = this.state;
  //   const { formData } = this.state;
  //   console.log(selectedPlace);
  //   this.setState({ loading: true });
  //   console.log("area mounted");
  //   authAxios
  //     .get(areaListURL(selectedPlace))
  //     .then(res => {
  //       this.setState({ areas: res.data, loading: false, selectedPlace: "" });
  //     })

  //     .catch(err => {
  //       this.setState({ error: err });
  //     });
  // };

  handleSelectChange = (e, { name, value }) => {
    // console.log("value");
    const { formData } = this.state;
    const updatedFormdata = {
      ...formData,
      [name]: value
    };
    this.setState({
      formData: updatedFormdata
    });
  };

  handleSubmit = e => {
    this.setState({ saving: true });
    e.preventDefault();
    const { formType } = this.props;
    if (formType === UPDATE_FORM) {
      this.handleUpdateAddress();
    } else {
      this.handleCreateAddress();
    }
  };

  handleCreateAddress = () => {
    const { userID, activeItem } = this.props;
    const { formData } = this.state;
    authAxios
      .post(addressCreateURL, {
        ...formData,
        user: userID,
        address_type: "S"
      })
      .then(res => {
        this.setState({
          saving: false,
          success: true,
          formData: { default: false }
        });
        this.props.callback();
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };

  handleUpdateAddress = () => {
    const { userID, activeItem } = this.props;
    const { formData } = this.state;
    authAxios
      .put(addressUpdateURL(formData.id), {
        ...formData,
        user: userID,
        address_type: "S"
      })
      .then(res => {
        this.setState({
          saving: false,
          success: true,
          formData: { default: false }
        });
        this.props.callback();
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };

  render() {
    const { classes } = this.props;
    const {
      error,
      formData,
      success,
      saving,
      selectedPlace,
      places,
      areas
    } = this.state;
    // console.log(selectedPlace);
    // console.log(formData.place);
    // console.log(places);
    // console.log(areas);

    return (
      <React.Fragment>
        <CssBaseline />
        {/* <Container> */}
        <Paper elevation={3} mt={10} mb={10} style={{ padding: "30px" }}>
          <br></br>
          <Typography variant="h5" gutterBottom>
            Address
          </Typography>
          <br></br>

          {success && (
            <Alert severity="success">
              <AlertTitle>Success</AlertTitle>
              Your address was saved
            </Alert>
          )}

          {error && (
            <Alert severity="error">
              <AlertTitle>{JSON.stringify(error)}</AlertTitle>
              This is an error alert — <strong>check it out!</strong>
            </Alert>
          )}
          <form
            noValidate
            onSubmit={this.handleSubmit}
            success={success}
            error={error}
          >
            <Grid container spacing={3}>
              {/* <Grid item xs={12}>
                <NativeSelect
                  style={{ display: "flex", justifyContent: "center" }}
                  name="place"
                  id="place"
                  onChange={this.handleChange}
                  inputProps={{ "aria-label": "Place" }}
                  fullWidth
                >
                  <option value="" disabled>
                    Select your place
                  </option>
                  {places.map(place => (
                    <option value={formData.place}>{place.name}</option>
                  ))}
                </NativeSelect>
              </Grid> */}
              <Grid item xs={12}>
                <TextField
                  required
                  id="place"
                  name="place"
                  label="Place Name"
                  fullWidth
                  autoComplete="place"
                  onChange={this.handleChange}
                  value={formData.place}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  id="area"
                  name="area"
                  label="Area Name"
                  fullWidth
                  autoComplete="area"
                  onChange={this.handleChange}
                  value={formData.area}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  id="house_name"
                  name="house_name"
                  label="House Name"
                  fullWidth
                  autoComplete="house_name"
                  onChange={this.handleChange}
                  value={formData.house_name}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  id="road_name"
                  name="road_name"
                  label="Road Name"
                  fullWidth
                  autoComplete="road_name"
                  onChange={this.handleChange}
                  value={formData.road_name}
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  id="village"
                  name="village"
                  label="Village"
                  fullWidth
                  // autoComplete="village"
                  onChange={this.handleChange}
                  value={formData.village}
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  id="district"
                  name="district"
                  label="District"
                  fullWidth
                  // autoComplete="district"
                  onChange={this.handleChange}
                  value={formData.district}
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  id="state"
                  name="state"
                  label="State"
                  fullWidth
                  onChange={this.handleChange}
                  value={formData.state}
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  id="pin_code"
                  name="pin_code"
                  label="Pin Code"
                  fullWidth
                  autoComplete="pin_code"
                  onChange={this.handleChange}
                  value={formData.pin_code}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  id="phone_number"
                  name="phone_number"
                  label="Phone Number"
                  fullWidth
                  autoComplete="phone_number"
                  onChange={this.handleChange}
                  value={formData.phone_number}
                />
              </Grid>
              <Grid item xs={12}>
                <FormControlLabel
                  value="best"
                  control={<Radio />}
                  label="Default Address"
                  name="default"
                  onChange={this.handleToggleDefault}
                  checked={formData.default}
                />
              </Grid>

              <Button
                variant="contained"
                color="primary"
                type="submit"
                disabled={saving}
                loading={saving}
                primary
              >
                Submit
              </Button>
            </Grid>
          </form>
        </Paper>
        {/* </Container> */}
      </React.Fragment>
    );
  }
}

class Profile extends React.Component {
  state = {
    activeItem: "shippingAddress",
    addresses: [],
    userID: null,
    selectedAddress: null
    // places: []
  };

  componentDidMount() {
    this.handleFetchAddresses();
    // this.handleFetchCountries();
    this.handleFetchUserID();
  }

  handleItemClick = name => {
    this.setState({ activeItem: name }, () => {
      // this.fetchPlaces();
    });
  };

  // fetchPlaces = () => {
  //   this.setState({ loading: true });
  //   console.log("mounted");
  //   authAxios
  //     .get(placeListURL)
  //     .then(res => {
  //       this.setState({ places: res.data, loading: false });
  //     })
  //     .catch(err => {
  //       this.setState({ error: err });
  //     });
  // };

  handleAddAddress = name => {
    this.setState({ activeItem: name }, () => {
      this.handleFetchAddresses();
    });
  };

  handleGetActiveItem = () => {
    const { activeItem } = this.state;
    if (activeItem === "addAddress") {
      return "addAddress";
    }
    return "Shipping Address";
  };

  handleDeleteAddress = addressID => {
    authAxios
      .delete(addressDeleteURL(addressID))
      .then(res => {
        this.handleCallback();
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };

  handleSelectAddress = address => {
    this.setState({ selectedAddress: address });
    // console.log(address);
  };

  handleFetchUserID = () => {
    authAxios
      .get(userIDURL)
      .then(res => {
        this.setState({ userID: res.data.userID });
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };
  handleFetchAddresses = () => {
    this.setState({ loading: true });
    const { activeItem } = this.state;
    authAxios
      .get(addressListURL(activeItem === "S"))
      // .get(addressListURL(activeItem === "billingAddress" ? "B" : "S"))
      .then(res => {
        this.setState({ addresses: res.data, loading: false });
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };

  handleCallback = () => {
    this.handleFetchAddresses();
  };

  renderAddresses = () => {
    const { activeItem, addresses, selectedAddress, userID } = this.state;

    return (
      // <Container className={classes.cardGrid}>
      <div>
        <Grid container spacing={1} maxWidth="md">
          {addresses.map(a => {
            return (
              <React.Fragment>
                <Grid item key={a.id} xs={12} sm={6} md={4}>
                  <Card>
                    <CardContent>
                      {a.default && (
                        <Badge badgeContent={"Default"} color="primary"></Badge>
                      )}
                      {/* <FormControlLabel
                        value="best"
                        control={<Radio />}
                        label="Default Address"
                        name="default"
                        onChange={this.handleToggleDefault}
                        checked={formData.default}
                      /> */}
                      <Typography variant="h5" component="h2">
                        {a.place}
                      </Typography>
                      <br />
                      <Typography color="textPrimary">
                        {a.house_name} {a.area} {a.roa}
                      </Typography>
                      <Typography variant="body2" component="p">
                        {a.village}
                        <br />
                        {a.pin_code}
                      </Typography>
                    </CardContent>
                    <CardActions>
                      <IconButton>
                        <EditIcon onClick={() => this.handleSelectAddress(a)} />
                      </IconButton>
                      <IconButton aria-label="Delete">
                        <DeleteSharpIcon
                          onClick={() => this.handleDeleteAddress(a.id)}
                        />
                      </IconButton>
                    </CardActions>
                  </Card>
                </Grid>
              </React.Fragment>
            );
          })}
        </Grid>
      </div>
    );
  };

  render() {
    const { classes } = this.props;
    // const bull = <span className={classes.bullet}>•</span>;
    const {
      activeItem,
      error,
      loading,
      userID,
      addresses,
      selectedAddress
      // places
    } = this.state;

    const { isAuthenticated } = this.props;
    // console.log(places);
    // if (!isAuthenticated) {
    //   return <Redirect to="/login" />;
    // }
    return (
      <React.Fragment>
        <Container className={classes.cardGrid} maxWidth="md">
          {loading && (
            <div
              align="middle"
              className={classes.loading}
              style={{ display: "flex", justifyContent: "center" }}
            >
              <CircularProgress />
            </div>
          )}
          {error && (
            <Alert severity="error">
              <AlertTitle>{JSON.stringify(error)}</AlertTitle>
              This is an error alert — <strong>check it out!</strong>
            </Alert>
          )}
          <Paper square className={classes.tab}>
            <Tabs
              variant="fullWidth"
              indicatorColor="secondary"
              textColor="secondary"
              aria-label="icon label tabs example"
            >
              <Tab
                icon={<ListIcon />}
                label="My Delivery Addresses "
                name="shippingAddress"
                active={activeItem === "shippingAddress"}
                onClick={() => this.handleItemClick("shippingAddress")}
              />
              <Tab
                icon={<AddIcon />}
                label="Add Address"
                name="addAddress"
                active={activeItem === "addAddress"}
                onClick={() => this.handleItemClick("addAddress")}
              />
            </Tabs>
          </Paper>
          <br></br>
          {activeItem === "addAddress" ? (
            <AddressForm
              activeItem={activeItem}
              formType={CREATE_FORM}
              userID={userID}
              // places={places}
              callback={this.handleCallback}
            />
          ) : (
            this.renderAddresses()
          )}
          {selectedAddress && (
            <AddressForm
              activeItem={activeItem}
              userID={userID}
              address={selectedAddress}
              formType={UPDATE_FORM}
              // places={places}
              callback={this.handleCallback}
            />
          )}
        </Container>
      </React.Fragment>
    );
  }
}

