import React from "react";
import { connect } from "react-redux";
import { authAxios } from "../../utils";
import {
  addToCartURL,
  orderSummaryURL,
  orderItemDeleteURL,
  orderItemUpdateQuantityURL
} from "../../constants";

import {
  CheckoutItemContainer,
  ImageContainer,
  TextContainer,
  QuantityContainer,
  RemoveButtonContainer
} from "./checkout.styles";

class CheckoutItem extends React.Component {
  handleRemoveQuantityFromCart = slug => {
    authAxios
      .post(orderItemUpdateQuantityURL, { slug })
      .then(res => {
        this.handleFetchOrder();
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };

  handleAddToCart = (id, shop, place) => {
    this.setState({ loading: true });
    const { formData } = this.state;
    const variations = this.handleFormatData(formData);
    authAxios
      .post(addToCartURL, { place, shop, id, variations })
      .then(res => {
        this.props.refreshCart();
        this.setState({ loading: false });
        this.check(authAxios);
      })
      .catch(err => {
        this.setState({ error: err, loading: false });
      });
  };

  handleRemoveItem = itemID => {
    authAxios
      .delete(orderItemDeleteURL(itemID))
      .then(res => {
        this.handleFetchOrder();
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };
  render() {
    const { order_item } = this.props;
    return (
      <CheckoutItemContainer>
        <TextContainer>{order_item.item.title}</TextContainer>
        <QuantityContainer>
          <div onClick={this.handleRemoveItem(order_item)}>&#10094;</div>
          <span>{order_item.quantity}</span>
          <div onClick={() => handleAddToCart(order_item)}>&#10095;</div>
        </QuantityContainer>
        <TextContainer>{order_item.final_price}</TextContainer>
      </CheckoutItemContainer>
    );
  }
}

export default CheckoutItem;
