import React from "react";
import { connect } from "react-redux";
import { Redirect } from "react-router-dom";
import TextField from "@material-ui/core/TextField";
import Container from "@material-ui/core/Container";
import CssBaseline from "@material-ui/core/CssBaseline";
// import Button from "@material-ui/core/Button";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
// import Grid from "@material-ui/core/Grid";
// import { makeStyles, withStyles } from "@material-ui/core/styles";
import { makeStyles, useTheme, withStyles } from "@material-ui/core/styles";
// import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import EditIcon from "@material-ui/icons/Edit";
import DeleteSharpIcon from "@material-ui/icons/DeleteSharp";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";

import {
  Button,
  Card,
  Dimmer,
  Divider,
  Form,
  Grid,
  Header,
  Image,
  Label,
  Loader,
  Menu,
  Message,
  Segment,
  Select,
  Table
} from "semantic-ui-react";
import {
  countryListURL,
  addressListURL,
  addressCreateURL,
  addressUpdateURL,
  addressDeleteURL,
  userIDURL,
  paymentListURL,
  orderListURL
} from "../../constants";
import { authAxios } from "../../utils";

const UPDATE_FORM = "UPDATE_FORM";
const CREATE_FORM = "CREATE_FORM";

const useStyles = makeStyles({
  root: {
    minWidth: 275
  },
  bullet: {
    display: "inline-block",
    margin: "0 2px",
    transform: "scale(0.8)"
  },
  title: {
    fontSize: 14
  },
  pos: {
    marginBottom: 12
  }
});

const styles = theme => ({
  paper: {
    display: "flex",
    flexWrap: "wrap",
    "& > *": {
      margin: theme.spacing(3)
    }
  },
  root: {
    minWidth: 275
  },
  bullet: {
    display: "inline-block",
    margin: "0 2px",
    transform: "scale(0.8)"
  },
  title: {
    fontSize: 14
  },
  pos: {
    marginBottom: 12
  }
});

class AddressForm extends React.Component {
  state = {
    error: null,
    loading: false,
    formData: {
      house_name: "",
      place: "",
      area: "",
      road_name: "",
      village: "",
      district: "",
      state: "",
      pin_code: "",
      phone_number: "",
      default: false,
      id: "",
      user: 1
    },
    saving: false,
    success: false
  };

  componentDidMount() {
    const { address, formType } = this.props;
    if (formType === UPDATE_FORM) {
      this.setState({ formData: address });
    }
  }

  handleToggleDefault = () => {
    const { formData } = this.state;
    const updatedFormdata = {
      ...formData,
      default: !formData.default
    };
    this.setState({
      formData: updatedFormdata
    });
  };

  handleChange = e => {
    const { formData } = this.state;
    const updatedFormdata = {
      ...formData,
      [e.target.name]: e.target.value
    };
    this.setState({
      formData: updatedFormdata
    });
  };

  handleSelectChange = (e, { name, value }) => {
    const { formData } = this.state;
    const updatedFormdata = {
      ...formData,
      [name]: value
    };
    this.setState({
      formData: updatedFormdata
    });
  };

  handleSubmit = e => {
    this.setState({ saving: true });
    e.preventDefault();
    const { formType } = this.props;
    if (formType === UPDATE_FORM) {
      this.handleUpdateAddress();
    } else {
      this.handleCreateAddress();
    }
  };

  handleCreateAddress = () => {
    const { userID, activeItem } = this.props;
    const { formData } = this.state;
    authAxios
      .post(addressCreateURL, {
        ...formData,
        user: userID,
        address_type: "S"
        // address_type: activeItem === "billingAddress" ? "B" : "S"
      })
      .then(res => {
        this.setState({
          saving: false,
          success: true,
          formData: { default: false }
        });
        this.props.callback();
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };

  handleUpdateAddress = () => {
    const { userID, activeItem } = this.props;
    const { formData } = this.state;
    authAxios
      .put(addressUpdateURL(formData.id), {
        ...formData,
        user: userID,
        address_type: "S"
        // address_type: activeItem === "billingAddress" ? "B" : "S"
      })
      .then(res => {
        this.setState({
          saving: false,
          success: true,
          formData: { default: false }
        });
        this.props.callback();
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };

  render() {
    const { classes } = this.props;
    // const { countries } = this.props;
    const { error, formData, success, saving } = this.state;
    return (
      <React.Fragment>
        <CssBaseline />
        <Container>
          <Paper elevation={3}>
            <br></br>
            <Typography variant="h5" gutterBottom>
              Address
            </Typography>
            <form
              //   className={classes.form}
              noValidate
              onSubmit={this.handleSubmit}
              success={success}
              error={error}
            >
              <Grid container spacing={3}>
                <Grid item xs={12} sm={6}>
                  <TextField
                    required
                    id="house_name"
                    name="house_name"
                    label="House Name"
                    fullWidth
                    autoComplete="house_name"
                    onChange={this.handleChange}
                    value={formData.house_name}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    required
                    id="place"
                    name="place"
                    label="Place Name"
                    fullWidth
                    autoComplete="place"
                    onChange={this.handleChange}
                    value={formData.place}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    required
                    id="area"
                    name="area"
                    label="Area Name"
                    fullWidth
                    autoComplete="area"
                    onChange={this.handleChange}
                    value={formData.area}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    id="road_name"
                    name="road_name"
                    label="Road Name"
                    fullWidth
                    autoComplete="road_name"
                    onChange={this.handleChange}
                    value={formData.road_name}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    required
                    id="village"
                    name="village"
                    label="Village"
                    fullWidth
                    // autoComplete="village"
                    onChange={this.handleChange}
                    value={formData.village}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    required
                    id="district"
                    name="district"
                    label="District"
                    fullWidth
                    // autoComplete="district"
                    onChange={this.handleChange}
                    value={formData.district}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    id="state"
                    name="state"
                    label="State"
                    fullWidth
                    onChange={this.handleChange}
                    value={formData.state}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    required
                    id="pin_code"
                    name="pin_code"
                    label="Pin Code"
                    fullWidth
                    autoComplete="pin_code"
                    onChange={this.handleChange}
                    value={formData.pin_code}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    required
                    id="phone_number"
                    name="phone_number"
                    label="Phone Number"
                    fullWidth
                    autoComplete="phone_number"
                    onChange={this.handleChange}
                    value={formData.phone_number}
                  />
                </Grid>

                {success && (
                  <Message
                    success
                    header="Success!"
                    content="Your address was saved"
                  />
                )}
                {error && (
                  <Message
                    error
                    header="There was an error"
                    content={JSON.stringify(error)}
                  />
                )}
                {/* <Form.Button disabled={saving} loading={saving} primary>
            Save
          </Form.Button> */}
                <Button
                  variant="contained"
                  color="primary"
                  type="submit"
                  disabled={saving}
                  loading={saving}
                  primary
                >
                  Submit
                </Button>
              </Grid>
            </form>
          </Paper>
        </Container>
      </React.Fragment>
    );
  }
}

function RenderAddresses(props) {
  const { activeItem, addresses, selectedAddress, userID } = props;

  const classes = useStyles();
  const bull = <span className={classes.bullet}>•</span>;

  return (
    <div>
      {addresses.map(a => {
        return (
          <React.Fragment>
            <Card className={classes.root}>
              <CardContent>
                <FormControlLabel
                  control={
                    <Checkbox
                      // checked={state.checkedB}
                      // onChange={handleChange}
                      name="checkedB"
                      color="primary"
                    />
                  }
                  label="Make this Default Address"
                />
                <Typography variant="h5" component="h2">
                  {a.place}
                </Typography>
                <br />
                <Typography className={classes.pos} color="textPrimary">
                  {a.house_name} {a.area} {a.roa}
                </Typography>
                <Typography variant="body2" component="p">
                  {a.village}
                  <br />
                  {a.pin_code}
                </Typography>
              </CardContent>
              <CardActions>
                <EditIcon />
                <DeleteSharpIcon />
                {/* <Button size="small">Learn More</Button> */}
              </CardActions>
            </Card>
          </React.Fragment>
        );
      })}
    </div>
  );
}

class Profile extends React.Component {
  state = {
    activeItem: "shippingAddress",
    addresses: [],
    userID: null,
    selectedAddress: null
  };

  componentDidMount() {
    this.handleFetchAddresses();
    this.handleFetchCountries();
    this.handleFetchUserID();
  }

  handleItemClick = name => {
    this.setState({ activeItem: name }, () => {
      this.handleFetchAddresses();
    });
  };

  handleGetActiveItem = () => {
    const { activeItem } = this.state;
    if (activeItem === "billingAddress") {
      return "Billing Address";
    } else if (activeItem === "shippingAddress") {
      return "Shipping Address";
    } else if (activeItem === "addAddress") {
      return "addAddress";
    }
    return "Shipping Address";
  };

  handleFormatCountries = countries => {
    const keys = Object.keys(countries);
    return keys.map(k => {
      return {
        key: k,
        text: countries[k],
        value: k
      };
    });
  };

  handleDeleteAddress = addressID => {
    authAxios
      .delete(addressDeleteURL(addressID))
      .then(res => {
        this.handleCallback();
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };

  handleSelectAddress = address => {
    this.setState({ selectedAddress: address });
  };

  handleFetchUserID = () => {
    authAxios
      .get(userIDURL)
      .then(res => {
        this.setState({ userID: res.data.userID });
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };

  handleFetchCountries = () => {
    authAxios
      .get(countryListURL)
      .then(res => {
        this.setState({ countries: this.handleFormatCountries(res.data) });
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };

  handleFetchAddresses = () => {
    this.setState({ loading: true });
    const { activeItem } = this.state;
    authAxios
      .get(addressListURL(activeItem === "S"))
      // .get(addressListURL(activeItem === "billingAddress" ? "B" : "S"))
      .then(res => {
        this.setState({ addresses: res.data, loading: false });
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };

  handleCallback = () => {
    this.handleFetchAddresses();
    this.setState({ selectedAddress: null });
  };

  render() {
    //  const { classes } = this.props;
    // const bull = <span className={classes.bullet}>•</span>;
    const {
      activeItem,
      error,
      loading,
      userID,
      addresses,
      selectedAddress
    } = this.state;
    const { isAuthenticated } = this.props;
    // if (!isAuthenticated) {
    //   return <Redirect to="/login" />;
    // }
    return (
      <Grid container columns={3} divided>
        <Grid.Row columns={2}>
          <Grid.Column>
            {error && (
              <Message
                error
                header="There was an error"
                content={JSON.stringify(error)}
              />
            )}
            {loading && (
              <Segment>
                <Dimmer active inverted>
                  <Loader inverted>Loading</Loader>
                </Dimmer>
                <Image src="/images/wireframe/short-paragraph.png" />
              </Segment>
            )}
          </Grid.Column>
        </Grid.Row>
        <Grid.Row>
          <Grid.Column width={12}>
            <Menu>
              {/* <Menu.Item
                name="Billing Address"
                active={activeItem === "billingAddress"}
                onClick={() => this.handleItemClick("billingAddress")}
              /> */}
              <Menu.Item
                name="Your Delivery Addresses"
                active={activeItem === "shippingAddress"}
                onClick={() => this.handleItemClick("shippingAddress")}
              />
              <Menu.Item
                name="addAddress"
                active={activeItem === "addAddress"}
                onClick={() => this.handleItemClick("addAddress")}
              />
            </Menu>
          </Grid.Column>
          <Grid.Column width={10}>
            <Header>{this.handleGetActiveItem()}</Header>
            <Divider />
            {activeItem === "addAddress" ? (
              <AddressForm
                activeItem={activeItem}
                formType={CREATE_FORM}
                userID={userID}
                callback={this.handleCallback}
              />
            ) : (
              <RenderAddresses
                activeItem={activeItem}
                addresses={addresses}
                selectedAddress={selectedAddress}
                userID={userID}
              />
              // this.renderAddresses()
            )}
          </Grid.Column>
        </Grid.Row>
      </Grid>
    );
  }
}

const mapStateToProps = state => {
  return {
    isAuthenticated: state.auth.token !== null
  };
};

export default connect(mapStateToProps)(Profile);
