import React from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { makeStyles } from "@material-ui/core/styles";
import Box from "@material-ui/core/Box";
import Collapse from "@material-ui/core/Collapse";
import IconButton from "@material-ui/core/IconButton";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Typography from "@material-ui/core/Typography";
import Paper from "@material-ui/core/Paper";
import KeyboardArrowDownIcon from "@material-ui/icons/KeyboardArrowDown";
import KeyboardArrowUpIcon from "@material-ui/icons/KeyboardArrowUp";
import Container from "@material-ui/core/Container";
import Icon from "@material-ui/core/Icon";

import { userIDURL, userTypeURL, orderListURL } from "../../constants";

import { authAxios } from "../../utils";

const useRowStyles = makeStyles({
  root: {
    "& > *": {
      borderBottom: "unset",
      margin: "20px",
      padding: "20px"
    },
    orderDtails: {
      borderBottom: "unset",
      margin: "300px",
      padding: "20px"
    },
    table: {
      minWidth: 700
    }
  }
});

function DesideUserType(props) {
  const { orders } = props;
  const { UserType } = props;
  if (UserType == "is_delivery_staff") {
    return (
      <div>
        <CustomerTable orders={orders} />
      </div>
    );
  } else if (UserType == "is_shop_owner") {
    return (
      <div>
        <OwnerTable orders={orders} />
      </div>
    );
  } else {
    return (
      <div>
        <DeliveryStaffTable orders={orders} />
      </div>
    );
  }
}

function CustomerTable(props) {
  const { orders } = props;
  const { UserType } = props;
  const classes = useRowStyles();

  return (
    <Container maxWidth="md">
      <TableContainer component={Paper}>
        <Table aria-label="collapsible table" className={classes.table}>
          <TableHead>
            {/* { UserType === "Customer" ? <p>customer</p> : <p>not</p>} */}
            <TableRow>
              <TableCell />
              <Typography variant="h1" component="h2">
                <TableCell align="right">Shop Name</TableCell>
                <TableCell align="right">Total Amount</TableCell>
                <TableCell align="right">Date</TableCell>
                <TableCell align="right">Order Status</TableCell>
              </Typography>
            </TableRow>
          </TableHead>
          <TableBody>
            {orders.map(order => (
              <Customer key={order.id} order={order} />
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Container>
  );
}

function Customer(props) {
  const { order } = props;
  //   const { orders } = props;
  const [open, setOpen] = React.useState(false);
  const classes = useRowStyles();

  return (
    <React.Fragment>
      <TableRow className={classes.root}>
        <TableCell>
          <IconButton
            aria-label="expand row"
            size="small"
            onClick={() => setOpen(!open)}
          >
            {open ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
          </IconButton>
        </TableCell>
        <TableCell component="th" scope="row">
          {order.shop_name}
        </TableCell>
        {/* <TableCell align="right">{order.shop_name}</TableCell> */}
        <TableCell align="right">{order.total}</TableCell>
        <TableCell align="right">{order.start_date}</TableCell>
        <TableCell align="right">{order.order_status}</TableCell>
      </TableRow>
      <TableRow>
        <TableCell style={{ paddingBottom: 0, paddingTop: 0 }} colSpan={6}>
          <Collapse in={open} timeout="auto" unmountOnExit>
            <Box margin={1}>
              <Typography variant="h6" gutterBottom component="div">
                Order Details
              </Typography>
              <Table size="small" aria-label="purchases">
                <TableHead>
                  <TableRow>
                    <TableCell>Item</TableCell>

                    <TableCell align="right">Quantity</TableCell>
                    <TableCell align="right">Total price</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {order.order_items.map(order_item => (
                    <TableRow key={order_item.id}>
                      <TableCell component="th" scope="row">
                        {order_item.item.title}
                      </TableCell>

                      <TableCell align="right">{order_item.quantity}</TableCell>
                      <TableCell align="right">
                        {order_item.final_price}
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </Box>
          </Collapse>
        </TableCell>
      </TableRow>
    </React.Fragment>
  );
}

/// customer table end

// shop owner table start

function OwnerTable(props) {
  const { orders } = props;
  const { UserType } = props;

  return (
    <Container maxWidth="sm">
      <TableContainer component={Paper}>
        <Table aria-label="collapsible table">
          <TableHead>
            {/* { UserType === "Customer" ? <p>customer</p> : <p>not</p>} */}
            <TableRow>
              <TableCell />
              <TableCell align="right">Customer Name</TableCell>
              <TableCell align="right">Total Amount</TableCell>
              <TableCell align="right">Date</TableCell>
              <TableCell align="right">Order Status</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {orders.map(order => (
              <Owner key={order.id} order={order} />
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Container>
  );
}

function Owner(props) {
  const { order } = props;
  //   const { orders } = props;
  const [open, setOpen] = React.useState(false);
  const classes = useRowStyles();

  return (
    <React.Fragment>
      <TableRow className={classes.root}>
        <TableCell>
          <IconButton
            aria-label="expand row"
            size="small"
            onClick={() => setOpen(!open)}
          >
            {open ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
          </IconButton>
        </TableCell>
        <TableCell component="th" scope="row">
          {order.customer_name}
        </TableCell>
        {/* <TableCell align="right">{order.shop_name}</TableCell> */}
        <TableCell align="center">{order.total}</TableCell>
        <TableCell align="center">{order.start_date}</TableCell>
        <TableCell align="center">{order.order_status}</TableCell>
      </TableRow>
      <TableRow>
        <TableCell style={{ paddingBottom: 0, paddingTop: 0 }} colSpan={6}>
          <Collapse in={open} timeout="auto" unmountOnExit>
            <Box margin={1}>
              <Typography variant="h6" gutterBottom component="div">
                Order Details
              </Typography>
              <Table size="small" aria-label="purchases">
                <TableHead>
                  <TableRow>
                    <TableCell>Item</TableCell>

                    <TableCell align="right">Quantity</TableCell>
                    <TableCell align="right">Total price</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {order.order_items.map(order_item => (
                    <TableRow key={order_item.id}>
                      <TableCell component="th" scope="row">
                        {order_item.item.title}
                      </TableCell>

                      <TableCell align="right">{order_item.quantity}</TableCell>
                      <TableCell align="right">
                        {order_item.final_price}
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </Box>
          </Collapse>
        </TableCell>
      </TableRow>
    </React.Fragment>
  );
}

// shop owner tabel end

/// delivery staff start

function DeliveryStaffTable(props) {
  const { orders } = props;
  const { UserType } = props;

  return (
    <Container maxWidth="md">
      <TableContainer component={Paper}>
        <Table aria-label="collapsible table">
          <TableHead>
            {/* { UserType === "Customer" ? <p>customer</p> : <p>not</p>} */}
            <TableRow>
              <TableCell />
              <TableCell align="right">Shop Name</TableCell>
              <TableCell align="right">Customer Name</TableCell>
              <TableCell align="right">Total Amount</TableCell>
              <TableCell align="right">Date</TableCell>
              <TableCell align="right">Order Status</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {orders.map(order => (
              <DeliveryStaff key={order.id} order={order} />
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Container>
  );
}

function DeliveryStaff(props) {
  const { order } = props;
  //   const { orders } = props;
  const [open, setOpen] = React.useState(false);
  const classes = useRowStyles();

  return (
    <React.Fragment>
      <TableRow className={classes.root}>
        <TableCell>
          <IconButton
            aria-label="expand row"
            size="small"
            onClick={() => setOpen(!open)}
          >
            {open ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
          </IconButton>
        </TableCell>
        <TableCell component="th" scope="row">
          {order.shop_name}
        </TableCell>
        <TableCell align="center">{order.customer_name}</TableCell>
        <TableCell align="center">{order.total}</TableCell>
        <TableCell align="center">{order.start_date}</TableCell>
