  );
}

// delivery staff end

class Orders extends React.Component {
  state = {
    orders: [],
    userID: null,
    UserType: null
  };

  componentDidMount() {
    this.handleFetchOrders();
    this.handleFetchUserID();
    this.handleFetchUserType();
  }

  handleFetchUserID = () => {
    authAxios
      .get(userIDURL)
      .then(res => {
        this.setState({ userID: res.data.userID });
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };

  handleFetchUserType = () => {
    authAxios
      .get(userTypeURL)
      .then(res => {
        this.setState({ UserType: res.data.UserType });
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };

  handleFetchOrders = () => {
    this.setState({ loading: true });
    authAxios
      .get(orderListURL)
      .then(res => {
        this.setState({
          loading: false,
          orders: res.data
        });
      })
      .catch(err => {
        this.setState({ error: err, loading: false });
      });
  };

  render() {
    const { orders } = this.state;
    const { userID } = this.state;
    const { UserType } = this.state;

    return (
      <div>
        <DesideUserType orders={orders} UserType={UserType} />
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    isAuthenticated: state.auth.token !== null
  };
};

export default connect(mapStateToProps)(Orders);
